D_diff = which( ! is.na(tree_4@data$D) == is.na(tree_4_raw@data$D) )

test_that("all modified nodes were originally speciation events", {
	expect_true( all( tree_4_raw@data$D[ D_diff ] == "N" ) )
} )

test_that("all modified nodes have descendant speciation node with same name", {
	x = sapply( D_diff, function( n ){
			progeny = unlist( Descendants( tree_4@phylo, n, type="all" ) )
			progeny = progeny[ progeny %in% which ( tree_4@data$D == "N" ) ]
			names = tree_4@data$label[ progeny ]
			tree_4@data$label[ n ] %in% names
		} )
	expect_true( all( x ) )
} )



